/**
 * HERE runtime configuration for development.
 *
 * For local testing you can set the API key in `src/index.html` using the
 * `window.__HERE_API_KEY__` global (see index.html for the placeholder).
 * For production, prefer build-time or server-side injection and DO NOT commit
 * real keys to the repository.
 */
export const HERE_CONFIG = {
  apiKey: typeof window !== 'undefined' && window.__HERE_API_KEY__ || 'REPLACE_WITH_YOUR_HERE_API_KEY'
};
// Helper to quickly detect an unconfigured key at runtime
export function isApiKeyPlaceholder() {
  return !HERE_CONFIG.apiKey || HERE_CONFIG.apiKey === 'REPLACE_WITH_YOUR_HERE_API_KEY';
}
